Rules.InvariantScript:
Reset(filterOrderStatus);
UpdateContext({lvOrderFilter: 1});
   Set(
        gvOrderStatusID,
        fMagicNo.gvOrderStatusCLO
    );
    Set(
        gvOrderFilterDate,
        Blank()
    );
    Set(
        gvOrderEmployeeID,
        0
    );
Set(
    gvTGLCRUDOrderList,
    !gvTGLCRUDOrderList
);

UpdateContext(
    {
        lvOrderFilter: Switch(
            Text(filterOrderStatus.Selected.Value),
            "Open Orders",
            1,
            "Recent Orders",
            2,
            "Internet Orders",
            3,
            "All Orders",
            4
        )
    }
);
If(
    lvOrderFilter = 1,
    Set(
        gvFilterDemo,
        fMagicNo.gvOrderStatusCLO
    );
    Set(
        gvOrderStatusID,
        fMagicNo.gvOrderStatusCLO
    );
    Set(
        gvOrderFilterDate,
        Blank()
    );
    Set(
        gvOrderEmployeeID,
        0
    );

);
If(
    lvOrderFilter = 2,
    Set(
        gvFilterDemo,
        0
    );
    Set(
        gvOrderStatusID,
        0
    );
    Set(
        gvOrderFilterDate,
        Text(
            Today(),
            "yyyy-mm-dd"
        )
    );
    Set(
        gvOrderEmployeeID,
        0
    );

);
If(
    lvOrderFilter = 3,
    Set(
        gvFilterDemo,
        0
    );
    Set(
        gvOrderStatusID,
        0
    );
    Set(
        gvOrderFilterDate,
        Blank()
    );
    Set(
        gvOrderEmployeeID,
        10
    );

);
If(
    lvOrderFilter = 4,
    Set(
        gvFilterDemo,
        0
    );
    Set(
        gvOrderStatusID,
        0
    );
    Set(
        gvOrderFilterDate,
        Blank()
    );
    Set(
        gvOrderEmployeeID,
        0
    );

);
Set(
    gvCRUDType,
    fMagicNo.gvCRUDRO
);
Set(
    gvTGLCRUDOrderList,
    !gvTGLCRUDOrderList
);

Set(
    gvCRUDType,
    fMagicNo.gvCRUDC //* C = Create, the 1st position in CRUD
);
Set(
    gvCRUDOrder,
    Blank()
);
Set(
    gvCRUDOrderID,
    0
);
//* Formatting of ship fee for the Orders Detail in order to show the Dollars and cents.
Set(
    gvShipFeeFormat,
    Text(
        0,
        "$#,###.00",
        "en-US"
    )
);
Navigate(scrOrderAdd);

SortByColumns (
    With(
        {
            records: SortByColumns(
                'gvCRUDOrderList',
                "OrderDate",
                SortOrder.Descending 
            )
        },
        ForAll(
            Sequence(CountRows(records)),
            Patch(
                Last(
                    FirstN(
                        records,
                        Value
                    )
                ),
                {rowNumber: Value}
            )
        )
    ),
    "OrderDate",
    SortOrder.Descending,
    "rowNumber",
    SortOrder.Ascending
)

Select(Parent);
Set(
    gvCRUDType,
    fMagicNo.gvCRUDR  
);
Set(
    gvCRUDOrder,
    ThisItem
);
Set(
    gvCRUDOrderID,
    gvCRUDOrder.OrderID
);

//* Formatting of ship fee for the Orders Detail in order to show the Dollars.
Set(
    gvShipFeeFormat,
    Text(
        Coalesce(
            Value(ThisItem.ShippingFee),
            0
        ),
        "$#,###.00",
        "en-US"
    )
);
Navigate(scrOrderAdd);
